package com.cho.var;

public class Var_04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1 = 10;
		num1 = num1 + 10; // 빈칸 포함해서 총 17글자
		
		
		num1 += 10; // 위와 같은 코드지만 용량 줄이기 성공
		
		num1 -= 10; // rg?
		
		num1 *= 3; //
		
		num1 /= 3; //
		num1 %= 3; //
		
		num1 ++; // += 1; 과 같은 코드 ( +1 효과 )
		num1 --; // -= 1; 과 같은 코드 ( -1 효과 )
		

	}

}
